mapper.update:
update user
	set update_at = CURRENT_TIMESTAMP
	where email = #{email}

update user
	set password = #{password}
	where user_idx = #{userIdx}


	update user
		set nickname = #{nickname}, introduce = #{introduce}, disclosure = #{disclosure}
		where user_idx = #{userIdx};


	delete from user_tag
		where user_idx = #{userIdx};

        insert into user_tag (user_idx, content)
        	values

        (
            #{userIdx},
            #{content}
        )
